import React from "react";
import Footer from "../Footer";
import backgroundImage from "../../images/bg.jpg";
import logo from "../../icons/logo.svg";
import {signinFooterlinks} from "../../data/links";

function Login(props) {
  const [isChecked, setChecked] = React.useState(true);

  function handleCheck(){
    setChecked(!isChecked);
  }

  return (
    <div className="page-container">
      {header()}
      <div className="login-body">
        <div className="login-container">
          {loginTop(props.email)}
          {loginBottom()}
        </div>
      </div>
      <div className="sigin-footer-wrapper">
        <Footer
          title={() =>{
            return(
              <p>Questions? Call<a className="footer-number" href="tel:1-844-505-2993">1-844-505-2993</a></p>
          )}}
          pageLinks={signinFooterlinks}/>
      </div>
    </div>);




    function header(){
      return (
        <header>
          <div className="login-background">
            <img className="login-image" src={backgroundImage} alt="Movie posters"></img>
            <div className="register-image-gradient"></div>
          </div>

          <div className="login-header">
            <a href="/"><img src={logo} alt="logo"></img></a>
          </div>
        </header>
      );
    }

    function loginTop(emailAddress){
      var title = "Sign In";
      if(emailAddress !== undefined){

      }

      return(
        <form method="post">
          <p className="login-title" >{title}</p>
          <div>
            <input className="login-input login-form-fields" type="email" placeholder="Email Address"></input>
          </div>
          <div>
            <input className="login-form-fields" type="password" placeholder="Password"></input>
          </div>
          <div>
            <button className="btn login-button login-form-fields" type="submit">
              <span className="">{title}</span>
            </button>
          </div>
          <div>
            <label className="checkbox-container">
              <div style={{"float": "left"}}>
                <input className="login-checkbox" onClick={handleCheck} type="checkbox" checked={isChecked}></input>
                <span>Remember me</span>
              </div>
              <a className="login-link" href="https://www.netflix.com/LoginHelp">Need help?</a>
            </label>
          </div>
        </form>
      );
    }

    function loginBottom(){
      return(
        <div className="login-container-bottom">
          <a href="">
            <img className="facebook-logo" src="https://assets.nflxext.com/ffe/siteui/login/images/FB-f-Logo__blue_57.png" alt="facebook logo"></img>
            <span>Login with Facebook</span>
          </a>
          <div style={{"margin-top": "10px"}}>
            <p>New to Netflix?<a href="/">Sign Up Now</a></p>
          </div>
        </div>
      );
    }
}

export default Login;
