import React from "react";
import headerLogo from "../../icons/logo.svg";
import Footer from "../Footer";
import homePagelinks from "../../data/links";
import RegisterationForm from "../RegisterationForm";
import backgroundImage from "../../images/bg.jpg";
// import StoryContainer from "./StoryContainer";
import QuestionsDisplay from "../QuestionsDisplay";
import questions from "../../data/questions";

function HomePage() {
  return (
    <div className="page-container">
      <header>
        {Header()}
      </header>
      <div className="body-container">
        {RegisterContainer()}
        <StoryContainer
          title="Enjoy on you TV."
          subtitle="Watch on Smart TVs, Playstation, Xbox, Chromecast, Apple TV, Blu-ray players, and more."
          cssImageCover="device-tv-img"
          imageCover="https://assets.nflxext.com/ffe/siteui/acquisition/ourStory/fuji/desktop/tv.png"
          imageContent="https://assets.nflxext.com/ffe/siteui/acquisition/ourStory/fuji/desktop/video-tv-0819.m4v"
          isFlipped={false}
          isVideo={true}/>
        <StoryContainer
          title="Download your shows to watch offline."
          subtitle="Save your favorites easily and always have something to watch."
          cssImageCover="device-phone-img"
          imageCover="https://assets.nflxext.com/ffe/siteui/acquisition/ourStory/fuji/desktop/mobile-0819.jpg"
          imageContent="https://assets.nflxext.com/ffe/siteui/acquisition/ourStory/fuji/desktop/boxshot.png"
          isFlipped={true}
          isVideo={false}/>
        <StoryContainer
          title="Watch everywhere."
          subtitle="Stream unlimited movies and TV shows on your phone, tablet, laptop, and TV without paying more."
          cssImageCover="device-desktop-img"
          imageCover="https://assets.nflxext.com/ffe/siteui/acquisition/ourStory/fuji/desktop/device-pile.png"
          imageContent="https://assets.nflxext.com/ffe/siteui/acquisition/ourStory/fuji/desktop/video-devices.m4v"
          isFlipped={false}
          isVideo={true}/>
        <QuestionsDisplay questions={questions}/>
      </div>
      <Footer
        title={() =>{
          return(
            <p>
              Questions? Call
              <a className="footer-number" href="tel:1-844-505-2993">1-844-505-2993</a>
            </p>
          );
        }}
        pageLinks={homePagelinks}/>
  </div>)
}

function Header() {
  return (
    <header className="header-container-wrapper">
      <div className="header-container">
        <img src={headerLogo} alt="Netflix Logo"/>
        <a className="signIn-btn" href="/Login">Sign In</a>
      </div>
    </header>
  );
}

function RegisterContainer(){
  return(
    <div className="basic-container">
      <div className="register-image-container">
        <img className="register-image" src={backgroundImage} alt=""/>
        <div className="register-image-gradient"></div>
      </div>

      <div className="register-text">
        <h1 className="register-title">Unlimited movies, TV shows, and more.</h1>
        <h2 className="register-subtitle">Watch anywhere. Cancel anytime.</h2>

        <RegisterationForm />
      </div>
    </div>
  );

  function StoryContainer(props) {
    return (
      <div className="basic-container">
        <div className={"story-container " + (props.isFlipped ? "story-flip-contents" : "")}>

          <div className="story-text">
            <h1 className="story-title">{props.title}</h1>
            <h2 className="story-subtitle">{props.subtitle}</h2>
          </div>

          <div className="device-container-outer">
            <div className="device-container-inner">
              <img className="display-screen" src={props.imageCover} alt=""></img>
              <div className={props.cssImageCover}>
                {displayContent()}
              </div>
            </div>
          </div>
        </div>
      </div>
    );

    function displayContent() {
      var section;

      if(props.isVideo){
        section = (
          <video className="display-video" autoPlay={true} loop={true}>
            <source src={props.imageContent} type="video/mp4"/>
          </video>
        );
      } else {
        section = (
          <div className="phone-container">
            <img className="phone-movie-img" src={props.imageContent} alt=""></img>
            <div className="phone-text">
              <div className="phone-title">Stranger Things</div>
              <div className="phone-subtitle">Downloading</div>
            </div>
            <img className = "phone-download-img" src = "https://assets.nflxext.com/ffe/siteui/acquisition/ourStory/fuji/desktop/download-icon.gif" alt=""></img>
          </div>
        );
      }

      return section;
    }
  }
}

export default HomePage;
