import React from "react";

const Form = (props) => {
  const [isChecked, setChecked] = React.useState(true);
  const isSignIn = (props.title === "Sign In");

  function handleCheck(){
    setChecked(!isChecked);
  }
  
  return(
    <form method="post">
      <p className="login-title" >{props.title}</p>
      <div>
        <input className="login-input login-form-fields" type="email" placeholder="Email Address"></input>
      </div>
      <div>
        <input className="login-form-fields" type="password" placeholder="Password"></input>
      </div>
      <div>
        <button className="btn login-button login-form-fields" type="submit">
          <span className="">{props.title}</span>
        </button>
      </div>

      <div>
        <label className="checkbox-container">
          <div style={{"float": "left"}}>
            <input className="login-checkbox" onClick={handleCheck} type="checkbox" checked={isChecked}></input>
            <span>Remember me</span>
          </div>
          <a className="login-link" href="https://www.netflix.com/LoginHelp">Need help?</a>
        </label>
      </div>
      {/* {(() => {
        if(isSignIn){
          console.log(isSignIn);
          return(
            <div>
              <label className="checkbox-container">
                <div style={{"float": "left"}}>
                  <input className="login-checkbox" onClick={handleCheck} type="checkbox" checked={isChecked}></input>
                  <span>Remember me</span>
                </div>
                <a className="login-link" href="https://www.netflix.com/LoginHelp">Need help?</a>
              </label>
            </div>
          );
        }
      })()} */}
    </form>
  );
}

export default Form;
